/**************************************************************************************************
 * モジュール名　: ロガー
 * 概要　　　　　: ログ管理
 *************************************************************************************************/
var blnConsoleFlg = true;

var log4js = require('log4js');
log4js.configure('./my_modules/node_modules/config/log4js.json');

var loggerApp = log4js.getLogger('app'),
    loggerErr = log4js.getLogger('error');

var funcName = '';

// ログのプレフィックスとなる関数名を定義する
exports.setFuncName = function(in_str){
  funcName = in_str + ' ';
};

// アプリケーションログ
exports.info = function(in_str){
  if(blnConsoleFlg){
    console.log(funcName + in_str);
  }else{
    loggerApp.info(funcName + in_str);
  }
};
// エラーログ（警告レベル）
exports.warn = function(in_str){
  if(blnConsoleFlg){
    console.log(funcName + in_str);
  }else{
    loggerErr.warn(funcName + in_str);
  }
};
// エラーログ（エラーレベル）
exports.error = function(in_str){
  if(blnConsoleFlg){
    console.log(funcName + in_str);
  }else{
    loggerErr.error(funcName + in_str);
  }
};
// エラーログ（緊急レベル）
exports.fatal = function(in_str){
  if(blnConsoleFlg){
    console.log(funcName + in_str);
  }else{
    loggerErr.fatal(funcName + in_str);
  }
};
